package com.example.my_dz_6

import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.recyclerview.widget.GridLayoutManager
import com.example.my_dz_6.databinding.FragmentMainBinding

class MainFragment : Fragment() {
private lateinit var adapter: LessonAdapter
private val ddd = arrayListOf<modelka>()
private lateinit var binding: FragmentMainBinding
    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View {
        binding = FragmentMainBinding.inflate(LayoutInflater.from(requireContext()),container,false)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        loadData()
        adapter= LessonAdapter(ddd)
        binding.recycler.layoutManager = GridLayoutManager(requireContext(), 2)
        binding.recycler.adapter= adapter
    }

    private fun loadData() {
        ddd.add(modelka("History",R.color.orange,"https://gogeticon.net/files/2097675/e7dcaeb11dc3b9ea3b99495a57f53896.png"))
        ddd.add(modelka("Physic", R.color.blue,"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQwjfQDkZVTLXzu4wqFU-h2STv0t4FeAB0DHg&usqp=CAU"))
        ddd.add(modelka("Math", R.color.green,"data:image/png;base64,"))
        ddd.add(modelka("English", R.color.purple,"data:image/jpeg;base64,"))
        ddd.add(modelka("French", R.color.white1,"data:image/jpeg;base64,"))
        ddd.add(modelka("Astronomy", R.color.brown,"data:image/jpeg;base64,"))
        ddd.add(modelka("Biology", R.color.white3,"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR1PpdV2D1Pqfwf7abAYcKnA50gY1fWiHGIKA&usqp=CAU"))
        ddd.add(modelka("Music", R.color.yellow,"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSGlRtTOCgPrXwUy4Qrss4J1q-WSsR4_gX0Gw&usqp=CAU"))
    }


}